package erp.cfd;

import java.util.ArrayList;
import java.util.Date;

/**
 * Enables a class to provide nodes and attributes for XML of CFDI 3.3.
 * Interface for classroom use generated by CFDI in SIIE (i.e SDataDps CFDI from income and SData)
 * @author Juan Barajas, Sergio Flores
 */
public interface SCfdXmlCfdi33 {

    public int getCfdType();
    public String getComprobanteVersion();  // Version
    public String getComprobanteSerie();    // Serie
    public String getComprobanteFolio();    // Folio
    public Date getComprobanteFecha();      // Fecha
    // Sello
    public String getComprobanteFormaPago();      // FormaPago
    // NoCertificado
    // Certificado
    public String getComprobanteCondicionesPago();  // CondicionesDePago
    public double getComprobanteSubtotal();         // SubTotal
    public double getComprobanteDescuento();        // Descuento
    public String getComprobanteMoneda();           // Moneda
    public double getComprobanteTipoCambio();       // TipoCambio
    public double getComprobanteTotal();            // Total
    public String getComprobanteTipoComprobante();  // TipoDeComprobante
    public String getComprobanteMetodoPago();       // MetodoPago
    public String getComprobanteLugarExpedicion();  // LugarExpedicion
    public String getComprobanteConfirmacion();     // Confirmacion
    public String getCfdiRelacionadosTipoRelacion();
    public ArrayList<String> getCfdiRelacionados();
    public int getEmisorId();
    public int getEmisorSucursalId();
    public String getEmisorRegimenFiscal();         // Emisor/RegimentFiscal
    public int getReceptorId();
    public int getReceptorSucursalId();
    public String getReceptorResidenciaFiscal();    // Receptor/RegimentFiscal
    public String getReceptorNumRegIdTrib();        // Receptor/NumRegIdTrib
    public String getReceptorUsoCFDI();             // Receptor/UsoCFDI
    public ArrayList<SCfdDataConcepto> getElementsConcepto() throws Exception;
    public ArrayList<SCfdDataImpuesto> getElementsImpuestos(float cfdiVersion);
    public cfd.DElement getElementComplemento() throws Exception;
    public cfd.DElement getElementAddenda();
}
